import axios from "axios";
import { useState} from 'react';
import { Route, Routes} from 'react-router-dom';
import General from "./Estilos/General.css"
import Cards from "./Componentes/Cards/Cards";
import Navegador from "./Componentes/Navegador/Navegador";



function App() {

  const [personajes, setPersonajes] = useState([]);

  function buscador(id){

    if (!id) {
      return; // Si no hay un ID, no hagas nada
    }

    const personajeExistente = personajes.some((elemento) => elemento.id === id);

    if (personajeExistente) {
      window.alert('¡Ya agregaste este Personaje!');
      return; // No continúes si el personaje ya existe
    }

    axios(`https://rickandmortyapi.com/api/character/${id}`)
    .then(({data})=>{

        setPersonajes([...personajes,{
          id: data.id,
          nombre: data.name,
          estado: data.status,
          especie: data.species,
          genero: data.gender,
          origen: data.origin.name,
          imagen: data.image
        }])
    })
    .catch(error => {
      window.alert('¡No hay personajes con este ID!');
    });
  }

  function eliminar(id){
    const nuevoPersonaje = personajes.filter((personaje) => personaje.id !== id);
    setPersonajes(nuevoPersonaje);
  }


  return (
    <div className="Fondo">
        <Navegador buscador={buscador}/>
        <Routes>
          <Route path="/" element={<Cards personajes={personajes} eliminar={eliminar}/>}/>
        </Routes>
    </div>
  );
}

export default App;
