import { useSelector } from "react-redux";
import Card from "../Card/Card";
import "./Estilos/Cards.css"

function Cards(){
    const personaje = useSelector((state) => state.personajes);
return(
    <div>
        <div className="Cards">
            {personaje.map(({id,nombre,estado,especie,origen,imagen,genero},index)=>{
                const uniqueKey = `${id}_${index}`;
                return <Card 
                id={id}
                key={uniqueKey}
                nombre={nombre} 
                estado={estado} 
                especie={especie} 
                origen={origen} 
                imagen={imagen}
                genero={genero}/>
            })}
        </div>
        {personaje.length > 10  && <div className="paginado">
            <button className="menor"> &lt; </button>
            <button className="mayor"> &gt; </button>
        </div>} 
    </div>
    )
}


export default Cards;
