import { useSelector } from "react-redux";
import Card from "../Card/Card";
import "./Estilos/Cards.css"

function Cards(){
    const personaje = useSelector((state) => state.personajes);
    return(
        <div className="Cards">
            {personaje.map(({id,nombre,estado,especie,origen,imagen,genero},index)=>{
                const uniqueKey = `${id}_${index}`;
                return <Card 
                id={id}
                key={uniqueKey}
                nombre={nombre} 
                estado={estado} 
                especie={especie} 
                origen={origen} 
                imagen={imagen}
                genero={genero}/>
            })}
        </div>
    )
}


export default Cards;
