import axios from "axios";

export const AGREGAR_PERSONAJE = "AGREGAR_PERSONAJE";
export const ELIMINAR_PERSONAJE = "ELIMINAR_PERSONAJE";

export function addPersonaje(id) {
  const URL = `https://rickandmortyapi.com/api/character/${id}`;

  return async (dispatch, getState) => {
    try {
      const { data } = await axios(`${URL}`);
      const state = getState(); 
      const verificador = state.personajes.find((e) => e.id === id);

      if (verificador) {
        return window.alert("¡Ya existe el personaje!");
      }

      return dispatch({
        type: AGREGAR_PERSONAJE,
        payload: {
          id: data.id,
          nombre: data.name,
          estado: data.status,
          especie: data.species,
          genero: data.gender,
          origen: data.origin.name,
          imagen: data.image,
        },
      });
    } catch (error) {
      return window.alert("No encuentro este personaje");
    }
  };
}

export function eliminar(id) {
  return {
    type: ELIMINAR_PERSONAJE,
    payload: id,
  };
}
