import React, {useState} from "react";

const ragexMail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i
const ragexPassword = /^(?=.*\d).{6,10}$/

function validation(userData){
    const errors = {}
    
    if(!ragexMail.test(userData.email)) errors.email = 'debe ser un mail valido';
    
    // if(!userData.email) errors.email = 'Esta vacio';
    
    if(userData.email.length > 25) errors.email = 'superaste los limites';
    
    if(!ragexPassword.test(userData.password)) errors.password = " Debe contener al menos un número y tener una longitud de 6 a 10 caracteres";

    
    
    return errors;
    }
    

    export default function Registro(props){

    const [userData, setUserData] = useState({
        email: "",
        password: "", 
    });

    const [errors, setErrors] = useState({});


    function handleChange(evento){

        const {name,value} = evento.target;

        setUserData({...userData, [name]: value})
    
        setErrors(validation({...userData, [name]: value}));

        }

    function handleSubmit(evento){
        if(errors.email || errors.password){
           return window.alert("Sigue las Instrucciones");
        }else{
            evento.preventDefault();
            props.verificador(userData);
        }
        
    }
    

    return(
        <div className="Inicio">

            <div className="Inicio-Dos">

            <form className="Formulario" onSubmit={handleSubmit}>
            <p className="Documentacion" >EMAIL</p>
            <input className="Secreto" type="text" placeholder="Email.." name="email" value={userData.email} onChange={handleChange}/>
            <p className="Error">{errors.email}</p>
            <br/>

            <p className="Documentacion">PASSWORD</p>
            <input className="Secreto" type="password" placeholder="Password.." name="password" value={userData.password} onChange={handleChange}/>
            <p className="Error">{errors.password}</p>
            <br/>

            <button className="Go">Go</button>
            </form>            

            </div>
           
        </div>

        
    )
}
