import {AGREGAR_PERSONAJE,ELIMINAR_PERSONAJE} from "./actions";

const initialState = {
    personajes: [],
}

function reducer(state = initialState, {type, payload}){
    switch(type){
        case AGREGAR_PERSONAJE: {
            const verificador = state.personajes.find((e)=> e.id === payload.id)

            if(!verificador){
                return {...state, personajes: [...state.personajes, payload]}
            }
            else window.alert('Ya existe el personaje')
        }

        case ELIMINAR_PERSONAJE: return { ...state, personajes: state.personajes.filter((e)=> e.id !== payload)}

        default:
            return {...state}
                
    }
}

export default reducer;
