import "./Estilos/Card.css";
import Like from "./Imagenes/Like.png";

function Card({id,nombre,estado,especie,origen,imagen,eliminar}){
    let estiloEstado = "";
    if(estado === "Dead"){
        estiloEstado = "Nombre-Rojo"
    }
    if(estado === "Alive"){
        estiloEstado = "Nombre-Verde"
    }
    if(estado === "unknown"){
        estiloEstado = "Nombre-Amarillo"
    }

    return(
        <div className="Card">

        <div className="Perfil">
            <button className="Eliminar" onClick={()=>eliminar(id)}>X</button>
            <img className="MeGusta" src={Like}/>
            <img className="Perfil-Imagen" src={imagen} alt={nombre}/>
            <span className="Nombre">{nombre}</span>
            <span className="Id" >{id}</span>
        </div>
        <div className="Texto">
            <p className={estiloEstado}>{estado}</p>
            <p className="Texto-Dos">{especie}</p>
            <p className="Texto-Dos">{origen}</p>
        </div>

        </div>
    )
}

export default Card;
