import { useState, useEffect} from 'react';
import { useSelector } from 'react-redux';
import Card from '../Card/Card';
import './Estilos/Cards.css';

function Cards() {
  const personajes = useSelector((state) => state.personajes);
  const [indice, setIndice] = useState(0);
  const [renderizado, setRenderizado] = useState(12);

  const sumar = () => {
    const newIndex = indice + 12;
    const newRenderizado = Math.min(renderizado + 12, personajes.length);
    setIndice(newIndex);
    setRenderizado(newRenderizado);
  };

  const restar = () => {
    const newIndex = Math.max(indice - 12, 0);
    const newRenderizado = Math.min(newIndex + 12, personajes.length);
    setIndice(newIndex);
    setRenderizado(newRenderizado);
  };

  let i = 0;

  useEffect(() => {
    if(renderizado < personajes.length){
      i = 1;
    }
  }, [personajes]);

  return (
    <div>
      <div className="Cards">
        {personajes.slice(indice, renderizado + i).map(({ id, nombre, estado, especie, origen, imagen, genero }, index) => {
          const uniqueKey = `${id}_${index}`;
          return (
            <Card
              id={id}
              key={uniqueKey}
              nombre={nombre}
              estado={estado}
              especie={especie}
              origen={origen}
              imagen={imagen}
              genero={genero}
            />
          );
        })}
      </div>
      <div className="paginado">
        {indice !== 0 && <button className="menor" onClick={restar}> &lt; </button>}
        {renderizado < personajes.length && <button className="mayor" onClick={sumar}> &gt; </button>}
      </div>
    </div>
  );
}

export default Cards;




