import { Route, Routes, useLocation, useNavigate  } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import General from "./Estilos/General.css";
import Cards from "./Componentes/Cards/Cards";
import Navegador from "./Componentes/Navegador/Navegador";
import Formulario from './Componentes/Formulario/Formulario';
import SobreMi  from './Componentes/SobreMi/SobreMi';
import Detalle from './Componentes/Detalle/Detalle';

function App() {

  const [access, setAcces] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();
  const homePag = location.pathname === '/';

  const EMAIL = 'RickAndMorty@gmail.com';
  const PASSWORD = `alien68`;

  useEffect(() => {
    !access && navigate('/');
 }, [access, navigate]);


  const login = async(userData)=>{
    try {
      const credenciales = await axios.post("http://localhost:3001/login",{email:userData.email, password:userData.password})

    } catch (error) {
      return window.alert('entre en el catch')
    }
  } 

  // function login(userData){
  //     if (userData.password === PASSWORD && userData.email === EMAIL) {
  //     setAcces(true)
  //     navigate('/Principal')}};

  return (
    <div className="Fondo">
      {!homePag && <Navegador/>}
      <Routes>
        <Route path='/' element={< Formulario login={login} EMAIL={EMAIL} PASSWORD={PASSWORD}/>} />  
        <Route path="/Principal" element={<Cards/>} />
        <Route path="/SobreMi" element={<SobreMi/>} />
        <Route path="/Detalle/:id" element={<Detalle/>}/>
      </Routes>
    </div>
  );
}

export default App;
