import axios from "axios";
import { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import General from "./Estilos/General.css";
import Cards from "./Componentes/Cards/Cards";
import Navegador from "./Componentes/Navegador/Navegador";

function App() {
  const [personajes, setPersonajes] = useState([]);

  useEffect(() => {
    console.log('El estado de personajes se actualizó:', personajes);
  }, [personajes]);

  async function verificador(id) {
    try {
      const analisis = personajes.find((personaje) => personaje.id === id);
      if (!analisis) {
        const response = await axios(`https://rickandmortyapi.com/api/character/${id}`);
        const data = response.data;
        // Verificar nuevamente después de la respuesta de la API
        const existente = personajes.find((personaje) => personaje.id === id);
        if (!existente) {
          setPersonajes((prevPersonajes) => [
            ...prevPersonajes,
            {
              id: data.id,
              nombre: data.name,
              estado: data.status,
              especie: data.species,
              genero: data.gender,
              origen: data.origin.name,
              imagen: data.image,
            },
          ]);
        } else {
          window.alert('¡Ya existe el personaje!');
        }
      } else {
        window.alert('¡Ya existe el personaje!');
      }
    } catch (error) {
      window.alert('¡No hay personajes con este ID!');
    }
  }

  function eliminar(id) {
    const nuevoPersonaje = personajes.filter((personaje) => personaje.id !== id);
    setPersonajes(nuevoPersonaje);
  }

  return (
    <div className="Fondo">
      <Navegador verificador={verificador} />
      <Routes>
        <Route path="/" element={<Cards personajes={personajes} eliminar={eliminar} />} />
      </Routes>
    </div>
  );
}

export default App;
