import {AGREGAR_PERSONAJE} from "./actions";

const initialState = {
    personajes: [],
}

function reducer(state = initialState, {type, payload}){
    switch(type){
        case AGREGAR_PERSONAJE: {
            const verificador = state.personajes.find((e)=> e.id === payload.id)

            if(verificador){
                return window.alert('¡Ya existe el personaje!');
            }

            else return {...state, personajes: [...state.personajes, payload]}

        }

        default:
            return {...state}
                
    }
}

export default reducer;
