import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink, Link } from 'react-router-dom';
import './Estilos/Card.css';
import Like from './Imagenes/Like.png';
import LikeRed from './Imagenes/LikeRojo.png';
import { eliminarFav, eliminar, agregarFav } from '../../Redux/actions';

function Card({ id, nombre, estado, especie, origen, imagen, genero }) {
  const [isFav, setIsFav] = useState(false);
  const user = useSelector((state) => state.usuario);
  const favU = useSelector((state) => state.favoritos);

  useEffect(() => {
    const verificador = favU.find((e) => e.id === id);
    setIsFav(!!verificador);
  }, [favU, id]);

  const dispatch = useDispatch();

  const obtenerEstiloEstado = (estado) => {
    if (estado === 'Dead') {
      return 'Nombre-Rojo';
    }
    if (estado === 'Alive') {
      return 'Nombre-Verde';
    }
    if (estado === 'unknown') {
      return 'Nombre-Amarillo';
    }
    return '';
  };

  const handleFavorite = () => {
    setIsFav((prevIsFav) => !prevIsFav);
  };

  useEffect(() => {
    if (isFav) {
      dispatch(eliminarFav(id, user));
    } else {
      dispatch(
        agregarFav({
          id: id,
          nombre: nombre,
          estado: estado,
          especie: especie,
          imagen: imagen,
          origen: origen,
          genero: genero,
          idUsuario: user,
        })
      );
    }
  }, [isFav, id, user, nombre, estado, especie, imagen, origen, genero, dispatch]);

  console.log(favU)

  return (
    <div className="Card">
      <div className="Perfil">
        <button className="Eliminar" onClick={() => dispatch(eliminar(id))}>
          X
        </button>
        {isFav ? (
          <img onClick={handleFavorite} className="MeGusta-Rojo" src={LikeRed} />
        ) : (
          <img onClick={handleFavorite} className="MeGusta" src={Like} />
        )}
        <NavLink to={`/Detalle/${id}`}>
          <img className="Perfil-Imagen" src={imagen} alt={nombre} />
        </NavLink>
        <Link className="Nombre" to={`/Detalle/${id}`}>
          {nombre}
        </Link>
        <span className="Id">{id}</span>
      </div>
      <div className="Texto">
        <p className={obtenerEstiloEstado(estado)}>{estado}</p>
        <p className="Texto-Dos">{especie}</p>
        <p className="Texto-Dos">{origen}</p>
      </div>
    </div>
  );
}

export default Card;
