import { Route, Routes, useLocation, useNavigate  } from 'react-router-dom';
import { useState, useEffect } from 'react';
import General from "./Estilos/General.css";
import Cards from "./Componentes/Cards/Cards";
import Navegador from "./Componentes/Navegador/Navegador";
import Formulario from './Componentes/Formulario/Formulario';

function App() {

  const [access, setAcces] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();
  const homePag = location.pathname === '/';

  const EMAIL = 'RickAndMorty@gmail.com';
  const PASSWORD = `alienigena68`;

  useEffect(() => {
    !access && navigate('/');
 }, [access, navigate]);

  function login(userData){
      if (userData.password === PASSWORD && userData.email === EMAIL) {
      setAcces(true)
      navigate('/home')}};

    let estilo = "";

    if(location.pathname === '/'){ estilo = "Login"}
    else estilo = "Fondo";

  return (
    <div className={estilo}>
      {!homePag && <Navegador/>}
      <Routes>
        <Route path='/' element={< Formulario login={login} EMAIL={EMAIL} PASSWORD={PASSWORD}/>} />  
        <Route path="/Principal" element={<Cards/>} />
      </Routes>
    </div>
  );
}

export default App;
