import React, { useState, useEffect } from 'react';

const MensajeEmergente = ({ mensaje }) => {
  const [mostrarMensaje, setMostrarMensaje] = useState(false);

  useEffect(() => {
    // El efecto se ejecuta cada vez que el mensaje cambia
    if (mensaje) {
      setMostrarMensaje(true);
    }
  }, [mensaje]);

  const cerrarMensajeHandler = () => {
    setMostrarMensaje(false);
  };

  // Renderiza el componente solo si mostrarMensaje es true y hay un mensaje
  return mostrarMensaje && mensaje && (
    <div className="mensaje-emergente">
      <button onClick={cerrarMensajeHandler}>X</button>
      <p>{mensaje}</p>
    </div>
  );
};

export default MensajeEmergente;
