import axios from "axios";
import { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import General from "./Estilos/General.css"
import Cards from "./Componentes/Cards/Cards";
import Navegador from "./Componentes/Navegador/Navegador";

function App() {
  const [personajes, setPersonajes] = useState([]);

  useEffect(() => {
    // Aquí puedes realizar acciones después de que se actualice personajes
    console.log('El estado de personajes se actualizó:', personajes);
  }, [personajes]);

  async function buscador(id) {
    try {
      const verificador = personajes.filter((e) => e.id === id)
      if (verificador.length > 0) {
        return window.alert('Ya agregaste este personaje!')
      }

      const { data } = await axios(`https://rickandmortyapi.com/api/character/${id}`);
      setPersonajes((prevPersonajes) => [
        ...prevPersonajes,
        {
          id: data.id,
          nombre: data.name,
          estado: data.status,
          especie: data.species,
          genero: data.gender,
          origen: data.origin.name,
          imagen: data.image,
        },
      ])
    } catch (error) {
      window.alert('¡No hay personajes con este ID!');
    }
  }

  function eliminar(id) {
    const nuevoPersonaje = personajes.filter((personaje) => personaje.id !== id);
    setPersonajes(nuevoPersonaje);
  }

  return (
    <div className="Fondo">
      <Navegador buscador={buscador} />
      <Routes>
        <Route path="/" element={<Cards personajes={personajes} eliminar={eliminar} />} />
      </Routes>
    </div>
  );
}

export default App;
