import React, {useState} from "react";
import "./Estilos/Estilos.css";
import RickPortal from "./Imagenes/RickPortal.png";

const ragexMail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i
const ragexPassword = /^(?=.*\d).{6,10}$/

function validation(userData){
    const errors = {}
    
    if(!ragexMail.test(userData.email)) errors.email = 'no se encuentra el mail';
    
    if(!userData.email) errors.email = 'Esta vacio';
    
    if(userData.email.length > 35) errors.email = 'superaste los limites';
    
    if(!ragexPassword.test(userData.password)) errors.password = "La contraseña debe contener al menos un número y tener una longitud de 6 a 10 caracteres";
    
    return errors;
    }
    

    export default function Formulario(props){

    const [userData, setUserData] = useState({
        email: "",
        password: "", 
    });

    const [errors, setErrors] = useState({});


    function handleChange(evento){

        const {name,value} = evento.target;

        setUserData({...userData, [name]: value})
    
        setErrors(validation({...userData, [name]: value}));

        }

    function handleSubmit(evento){
        evento.preventDefault();
        props.login(userData, props.PASSWORD, props.EMAIL);
    }
    

    return(
        <div className="Inicio">

            
            <form className="Formulario" onSubmit={handleSubmit}>
            <label>EMAIL</label>
            <input className="Email" type="text" placeholder="Email.." name="email" value={userData.email} onChange={handleChange}/>
            <p className="Error">{errors.email}</p>
            <br/>

            <label>PASSWORD</label>
            <input className="Password" type="password" placeholder="Password.." name="password" value={userData.password} onChange={handleChange}/>
            <p className="Error">{errors.password}</p>
            <br/>

            <button className="Go">Go</button>
            </form>

            <div className="Inicio-Dos">
                <img className="RickPortal" />
            </div>


        </div>

        
    )
}
