import { useDispatch } from "react-redux";
import { NavLink, Link } from "react-router-dom";
import "./Estilos/Card.css";
import Like from "./Imagenes/Like.png";
import { eliminar } from "../../Redux/actions";

function Card({id,nombre,estado,especie,origen,imagen}){

    let estiloEstado = "";
    if(estado === "Dead"){
        estiloEstado = "Nombre-Rojo"
    }
    if(estado === "Alive"){
        estiloEstado = "Nombre-Verde"
    }
    if(estado === "unknown"){
        estiloEstado = "Nombre-Amarillo"
    }

    const dispatch = useDispatch();

    return(
        <div className="Card">

        <div className="Perfil">
            <button className="Eliminar" onClick={ ()=>{dispatch(eliminar(id))} }>X</button>
            <img className="MeGusta" src={Like}/>
            <NavLink to="Detalle"><img className="Perfil-Imagen" src={imagen} alt={nombre}/></NavLink>
            <Link className="Nombre" to="/Detalle">{nombre}</Link>
            <span className="Id" >{id}</span>
        </div>
        <div className="Texto">
            <p className={estiloEstado}>{estado}</p>
            <p className="Texto-Dos">{especie}</p>
            <p className="Texto-Dos">{origen}</p>
        </div>

        </div>
    )
}

export default Card;
